mapper.update:
UPDATE orders
number = #{number}, status = #{status}, user_id = #{userId}, address_book_id = #{addressBookId}, order_time = #{orderTime}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, amount = #{amount}, remark = #{remark}, phone = #{phone}, address = #{address}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, pack_amount = #{packAmount}, tableware_number = #{tablewareNumber}, tableware_status = #{tablewareStatus}
WHERE id = #{id}

mapper.select:
SELECT
id, number, status, user_id, address_book_id,
order_time, checkout_time, pay_method, pay_status,
amount, remark, phone, address, consignee,
cancel_reason, rejection_reason, cancel_time,
estimated_delivery_time, delivery_status, delivery_time,
pack_amount, tableware_number, tableware_status
FROM orders



        AND id = #{id}


        AND number = #{number}


        AND status = #{status}


        AND user_id = #{userId}


        AND address_book_id = #{addressBookId}


        AND order_time = #{orderTime}


        AND checkout_time = #{checkoutTime}


        AND pay_method = #{payMethod}


        AND pay_status = #{payStatus}


        AND amount = #{amount}


        AND remark = #{remark}


        AND phone = #{phone}


        AND address = #{address}


        AND consignee = #{consignee}



ORDER BY order_time DESC

select DATE_FORMAT(order_time, '%Y-%m-%d') orderDate, sum(amount) orderMoney from orders
status = #{status}
and order_time between #{beginTime} and #{endTime}
group by DATE_FORMAT(order_time, '%Y-%m-%d') order by DATE_FORMAT(order_time, '%Y-%m-%d')

select DATE_FORMAT(order_time, '%Y-%m-%d') orderDate, count(*) orderCount from orders
status = #{status}
and order_time between #{beginTime} and #{endTime}
group by DATE_FORMAT(order_time, '%Y-%m-%d') order by DATE_FORMAT(order_time, '%Y-%m-%d')
