mapper.update:
UPDATE setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
WHERE id = #{id}

mapper.select:
select id, name, category_id, price, status, description, image, create_time, update_time, create_user, update_user from setmeal where 1=1
AND name LIKE CONCAT('%', #{name}, '%')
AND status = #{status}
AND category_id = #{categoryId}
order by create_time desc
